mapper.update:
UPDATE USERORDER_REFUND
SET
REFUND_STATUS = #{refund_status}
WHERE REFUND_ID= ${refund_id}

update users
set USER_POINT = USER_POINT - ( select
#{order_total} * 0.05
from
USER_ORDER o
where o.ORDER_ID = #{order_id}
)
where user_id = #{user_id}

mapper.select:
SELECT REFUND_STATUS
FROM USERORDER_REFUND
WHERE REFUND_ID = #{refund_id}
