mapper.insert:
INSERT INTO CART(
CART_ID, USER_ID, PRODUCT_ID,
CART_PRODUCT_QTY
) VALUES (
CART_SEQ.NEXTVAL, #{user_id}, #{product_id},
#{cart_product_qty}
)

mapper.update:
UPDATE CART
SET CART_PRODUCT_QTY = #{cart_product_qty}
WHERE USER_ID= #{user_id}
AND PRODUCT_ID = #{product_id}

mapper.select:
SELECT COUNT(*)
FROM CART
WHERE
USER_ID= #{user_id}
AND PRODUCT_ID = #{product_id}

mapper.update:
UPDATE CART
SET CART_PRODUCT_QTY = CART_PRODUCT_QTY +
#{cart_product_qty}
WHERE user_id= #{user_id}
AND product_id =
#{product_id}

mapper.select:
SELECT COUNT(CART_ID)
FROM CART
WHERE USER_ID = #{user_id}
